func ROT(source string) string {
	wordList := strings.Split(words, ",")
	// fmt.Println(len(wordList))
	if len(wordList) == 0 || len(wordList)%2 != 0 {
		panic("words is not even")
	}
	result := []string{}

	// 0 1
	// 2 3
	sourceList := strings.Split(source, "")

	for i := 0; i < len(sourceList); i++ {
		if ok, index := isInWords(sourceList[i], wordList); ok {
			var targetIndex int
			if index < (len(wordList) / 2) {
				// in left list
				targetIndex = index + (len(wordList) / 2)
			} else {
				// in right list
				targetIndex = index - (len(wordList) / 2)
			}
			result = append(result, wordList[targetIndex])

		} else {
			result = append(result, sourceList[i])
		}
	}
	return strings.Join(result, "")
}

func isInWords(w string, wordlist []string) (bool, int) {
	for index, item := range wordlist {
		if item == w {
			return true, index
		}
	}
	return false, -1
}
